package Grundlagen;

public class Testitest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String Text = ("Hello World!");
		
		System.out.println("Zeichen\t    ASCII-Wert");
		System.out.println("   " + Text.charAt(0)  + "\t\t "  + (int) Text.charAt(0)); 
		System.out.println("   " + Text.charAt(1)  + "\t\t" + (int) Text.charAt(1));
		System.out.println("   " + Text.charAt(2)  + "\t\t" + (int) Text.charAt(2));
		System.out.println("   " + Text.charAt(3)  + "\t\t" + (int) Text.charAt(3));
		System.out.println("   " + Text.charAt(4)  + "\t\t" + (int) Text.charAt(4));
		System.out.println("   " + Text.charAt(5)  + "\t\t " + (int) Text.charAt(5));
		System.out.println("   " + Text.charAt(6)  + "\t\t " + (int) Text.charAt(6));
		System.out.println("   " + Text.charAt(7)  + "\t\t" + (int) Text.charAt(7));
		System.out.println("   " + Text.charAt(8)  + "\t\t" + (int) Text.charAt(8));
		System.out.println("   " + Text.charAt(9)  + "\t\t" + (int) Text.charAt(9));
		System.out.println("   " + Text.charAt(10)  + "\t\t" + (int) Text.charAt(10));
		System.out.println("   " + Text.charAt(11)  + "\t\t " + (int) Text.charAt(11));
	}

}
